package modchu.eatsugar;import net.minecraft.creativetab.CreativeTabs;import net.minecraft.item.Item;import net.minecraft.item.ItemFood;import net.minecraft.potion.PotionHelper;import cpw.mods.fml.common.Mod;import cpw.mods.fml.common.Mod.EventHandler;import cpw.mods.fml.common.event.FMLPreInitializationEvent;@Mod(modid="EatSugar", name="EatSugar", version="2")public class EatSugar {	public String getVersion() {		return "1.6.4-2";	}	@EventHandler	public void preInit(FMLPreInitializationEvent event) {		Item.sugar = (new ItemFood(97, 2, 0.1F, false)).setUnlocalizedName("sugar").setPotionEffect(PotionHelper.sugarEffect).setCreativeTab(CreativeTabs.tabMaterials).setTextureName("sugar");	}}